DROP DATABASE IF EXISTS `school`;
CREATE DATABASE `school`;
USE `school`;

DROP TABLE IF EXISTS student;
CREATE TABLE student(
student_id CHAR(5) NOT NULL PRIMARY KEY
,student_name VARCHAR(14) NOT NULL
,sex VARCHAR(1) NOT NULL
,birth_date DATE NOT NULL
,address VARCHAR(30) NOT NULL
,major VARCHAR(7) NOT NULL
);

DROP TABLE IF EXISTS class;
CREATE TABLE class(
class_id VARCHAR(7) NOT NULL PRIMARY KEY,
class_name VARCHAR(12) NOT NULL,
grade INTEGER NOT NULL
);


DROP TABLE IF EXISTS subject_master;
CREATE TABLE subject_master(
subject_id CHAR(6) NOT NULL PRIMARY KEY
,subject_name VARCHAR(35) NOT NULL
,grade INTEGER NOT NULL
,passing_score INTEGER NOT NULL
);


DROP TABLE IF EXISTS subject_class;
CREATE TABLE subject_class(
   subject_class_id VARCHAR(12) NOT NULL PRIMARY KEY
  ,class_id         VARCHAR(6) NOT NULL
  ,academic_year    VARCHAR(9) NOT NULL
  ,subject_id       VARCHAR(5) NOT NULL,
FOREIGN KEY(class_id) REFERENCES class(class_id),
FOREIGN KEY(subject_id) REFERENCES subject_master(subject_id)
);


DROP TABLE IF EXISTS tuition;
CREATE TABLE tuition(
tuition_id VARCHAR(8) NOT NULL PRIMARY KEY
,grade INTEGER NOT NULL
);

DROP TABLE IF EXISTS parent;
CREATE TABLE parent(
student_id CHAR(5) NOT NULL PRIMARY KEY
,parent_name VARCHAR(8) NOT NULL
,sex CHAR(1) NOT NULL
,birthdate DATE NOT NULL
,relationship VARCHAR(12) NOT NULL,
FOREIGN KEY (student_id)
REFERENCES student(student_id)
);


DROP TABLE IF EXISTS student_tuition;
CREATE TABLE student_tuition(
transaction_id VARCHAR(11) NOT NULL PRIMARY KEY
,student_id CHAR(5) NOT NULL
,tuition_id VARCHAR(8) NOT NULL
,payment_date DATE
,payment_status VARCHAR(6) NOT NULL,
FOREIGN KEY (student_id) REFERENCES student(student_id),
FOREIGN KEY (tuition_id) REFERENCES tuition(tuition_id)
);


DROP TABLE IF EXISTS class_student;
CREATE TABLE class_student(
   class_id      VARCHAR(6) NOT NULL
  ,student_id    CHAR(5) NOT NULL
  ,academic_year VARCHAR(9) NOT NULL,
PRIMARY KEY(student_id,academic_year),
FOREIGN KEY (class_id) 
        REFERENCES class(class_id),
FOREIGN KEY (student_id) 
        REFERENCES student(student_id)
);

DROP TABLE IF EXISTS period;
CREATE TABLE period(
   period_id  VARCHAR(4) NOT NULL PRIMARY KEY
  ,start_hour VARCHAR(5) NOT NULL
  ,end_hour   VARCHAR(5) NOT NULL
  ,status     VARCHAR(23) NOT NULL
);



DROP TABLE IF EXISTS extracurricular;
CREATE TABLE extracurricular(
extracurricular_id VARCHAR(6) NOT NULL PRIMARY KEY
,extracurricular_name VARCHAR(12) NOT NULL
);

DROP TABLE IF EXISTS employee;
CREATE TABLE employee(
employee_id VARCHAR(4) NOT NULL PRIMARY KEY
,employeeFirstName VARCHAR(9) NOT NULL
,employeeLastName VARCHAR(9) NOT NULL
,start_date VARCHAR(10) NOT NULL
,sex VARCHAR(1) NOT NULL
,birth_date VARCHAR(10) NOT NULL
,address VARCHAR(22) NOT NULL
,employee_categ VARCHAR(13) NOT NULL
);

DROP TABLE IF EXISTS examination;
CREATE TABLE examination(
exam_id VARCHAR(9) NOT NULL PRIMARY KEY
,subject_id VARCHAR(5) NOT NULL
,component VARCHAR(50) NOT NULL
,type VARCHAR(3) NOT NULL
,academic_year VARCHAR(9) NOT NULL,
FOREIGN KEY(subject_id) REFERENCES subject_master(subject_id)
);

DROP TABLE IF EXISTS facility;
CREATE TABLE facility(
facility_id VARCHAR(6) NOT NULL PRIMARY KEY
,facility_name VARCHAR(19) NOT NULL
);

DROP TABLE IF EXISTS tuition_price_history;
CREATE TABLE tuition_price_history(
tuition_id VARCHAR(8) NOT NULL
,effective_date DATE NOT NULL
,price VARCHAR(9) NOT NULL,
PRIMARY KEY(tuition_id,effective_date),
FOREIGN KEY (tuition_id) REFERENCES tuition(tuition_id)
);


DROP TABLE IF EXISTS employee_attandance;
CREATE TABLE employee_attandance(
   employee_id       VARCHAR(4) NOT NULL
  ,date              DATE  NOT NULL
  ,attandance_detail VARCHAR(9) NOT NULL
,PRIMARY KEY(employee_id,date),
FOREIGN KEY(employee_id) REFERENCES employee(employee_id)
);


DROP TABLE IF EXISTS exam_schedule;
CREATE TABLE exam_schedule(
   exam_id   VARCHAR(9) NOT NULL
  ,period_id VARCHAR(4) NOT NULL
  ,date      DATE  NOT NULL
,FOREIGN KEY (exam_id)
REFERENCES examination(exam_id)
,FOREIGN KEY (period_id)
REFERENCES period(period_id)
);


DROP TABLE IF EXISTS class_facility;
CREATE TABLE class_facility(
class_id VARCHAR(6) NOT NULL
,facility_id VARCHAR(6) NOT NULL
,effective_date DATE NOT NULL
,PRIMARY KEY(class_id,effective_date)
);

DROP TABLE IF EXISTS student_attandance;
CREATE TABLE student_attandance(
student_id CHAR(5) NOT NULL
,date DATE NOT NULL
,attandance_detail VARCHAR(9) NOT NULL,
PRIMARY KEY(student_id, date),
FOREIGN KEY(student_id) REFERENCES class_student(student_id)
);


DROP TABLE IF EXISTS extracurricular_student;
CREATE TABLE extracurricular_student(
academic_year VARCHAR(9) NOT NULL
,student_id CHAR(5) NOT NULL
,extracurricular_id VARCHAR(6) NOT NULL
,PRIMARY KEY(student_id, academic_year)
,FOREIGN KEY (student_id)
REFERENCES student(student_id)
,FOREIGN KEY (extracurricular_id)
REFERENCES extracurricular(extracurricular_id)
);


DROP TABLE IF EXISTS extracurricular_teacher;
CREATE TABLE extracurricular_teacher(
extracurricular_id VARCHAR(6) NOT NULL
,effective_date DATE NOT NULL
,teacher_id VARCHAR(4) NOT NULL
,PRIMARY KEY(extracurricular_id, effective_date)
,FOREIGN KEY (extracurricular_id)
REFERENCES extracurricular(extracurricular_id)
,FOREIGN KEY (teacher_id)
REFERENCES employee(employee_id)
);

CREATE TABLE extracurricular_facility(
extracurricular_id VARCHAR(6) NOT NULL
,facility_id VARCHAR(6) NOT NULL
,effective_date DATE NOT NULL
,PRIMARY KEY (extracurricular_id, effective_date)
,FOREIGN KEY (extracurricular_id)
REFERENCES extracurricular(extracurricular_id)
,FOREIGN KEY (facility_id)
REFERENCES facility(facility_id)
);

DROP TABLE IF EXISTS subject_class;
CREATE TABLE subject_class(
subject_class_id VARCHAR(12) NOT NULL
,class_id VARCHAR(6) NOT NULL
,academic_year VARCHAR(9) NOT NULL
,subject_id VARCHAR(5) NOT NULL,
PRIMARY KEY(subject_class_id),
FOREIGN KEY (class_id)
REFERENCES class(class_id),
FOREIGN KEY (subject_id)
REFERENCES subject_master(subject_id)
);


DROP TABLE IF EXISTS subject_teacher;
CREATE TABLE subject_teacher(
   subject_class_id VARCHAR(12) NOT NULL
  ,teacher_id       VARCHAR(4) NOT NULL
  ,effective_date   DATE  NOT NULL,
PRIMARY KEY(subject_class_id, teacher_id, effective_date),
FOREIGN KEY(subject_class_id) REFERENCES subject_class(subject_class_id),
FOREIGN KEY(teacher_id) REFERENCES employee(employee_id)
);


DROP TABLE IF EXISTS extracurricular_score;
CREATE TABLE extracurricular_score(
student_id VARCHAR(5) NOT NULL
,extracurricular_id VARCHAR(6) NOT NULL
,academic_year VARCHAR(9) NOT NULL
,extracurricular_score INTEGER NOT NULL
,PRIMARY KEY(student_id, extracurricular_id, academic_year)
,FOREIGN KEY (student_id)
REFERENCES student(student_id)
,FOREIGN KEY (extracurricular_id)
REFERENCES extracurricular(extracurricular_id)
);

DROP TABLE IF EXISTS class_schedule;
CREATE TABLE class_schedule(
   period_id        VARCHAR(4) NOT NULL
  ,subject_class_id VARCHAR(12) NOT NULL
  ,`day`              VARCHAR(9) NOT NULL
,PRIMARY KEY(period_id,subject_class_id,`day`),
FOREIGN KEY(period_id) REFERENCES period(period_id),
FOREIGN KEY(subject_class_id) REFERENCES subject_class(subject_class_id)
);


DROP TABLE IF EXISTS cleaning_area;
CREATE TABLE cleaning_area(
janitor_id VARCHAR(4) NOT NULL
,facility_id VARCHAR(6) NOT NULL
,effective_date DATE NOT NULL
,PRIMARY KEY(janitor_id,effective_date),
FOREIGN KEY(janitor_id) REFERENCES employee(employee_id)
);


DROP TABLE IF EXISTS facility_details;
CREATE TABLE facility_details(                                                                                                 
facility_id VARCHAR(6) NOT NULL                                                                                                
,effective_date DATE NOT NULL                                                                                                  
,property VARCHAR(17) NOT NULL                                                                                                 
,quantity INTEGER NOT NULL                                                                                                     
,PRIMARY KEY(facility_id,effective_date,property),                                                                             
FOREIGN KEY(facility_id) REFERENCES facility(facility_id)                                                                      
);                                                                                                                             


DROP TABLE IF EXISTS facility_conditions;
CREATE TABLE facility_conditions(
f_report_id VARCHAR(8) NOT NULL PRIMARY KEY
,facility_id VARCHAR(6) NOT NULL
,facility_condition VARCHAR(8) NOT NULL
,date DATE NOT NULL
,note VARCHAR(34) NOT NULL,
FOREIGN KEY(facility_id) REFERENCES facility(facility_id)
);

DROP TABLE IF EXISTS score;                                                                                                      
CREATE TABLE score(                                                                                                              
student_id CHAR(5) NOT NULL                                                                                                      
,subject_class_id VARCHAR(12) NOT NULL                                                                                           
,input_date DATE NOT NULL                                                                                                        
,component VARCHAR(11) NOT NULL                                                                                                  
,score INTEGER NOT NULL,                                                                                                         
PRIMARY KEY(student_id,subject_class_id,component),                                                                              
FOREIGN KEY(student_id) REFERENCES student(student_id),                                                                          
FOREIGN KEY(subject_class_id) REFERENCES subject_class(subject_class_id)                                                         
);                                                                                                                               

DROP TABLE IF EXISTS entry_log;
CREATE TABLE entry_log(
visitor_id VARCHAR(5) NOT NULL
,date DATE NOT NULL
,entry_time VARCHAR(5) NOT NULL
,exit_time VARCHAR(5) NOT NULL
,purpose VARCHAR(23) NOT NULL
,facility_id VARCHAR(6) NOT NULL
,PRIMARY KEY(visitor_id, facility_id, date, entry_time)
,FOREIGN KEY (facility_id)
REFERENCES facility(facility_id)
);

DROP TABLE IF EXISTS extracurricular_schedule;
CREATE TABLE extracurricular_schedule(
extracurricular_id VARCHAR(6) NOT NULL
,academic_year VARCHAR(9) NOT NULL
,day VARCHAR(9) NOT NULL
,PRIMARY KEY(extracurricular_id,academic_year)
,FOREIGN KEY (extracurricular_id)
REFERENCES extracurricular(extracurricular_id)
);


DROP TABLE IF EXISTS extracurricular_schedule;
CREATE TABLE extracurricular_schedule(
extracurricular_id VARCHAR(6) NOT NULL
,academic_year VARCHAR(9) NOT NULL
,day VARCHAR(9) NOT NULL
,PRIMARY KEY(extracurricular_id,academic_year)
,FOREIGN KEY (extracurricular_id)
REFERENCES extracurricular(extracurricular_id)
);

DROP TABLE IF EXISTS extracurricular_attendance;
CREATE TABLE extracurricular_attendance(
student_id CHAR(5) NOT NULL
,date DATE NOT NULL
,attandance_detail VARCHAR(9) NOT NULL
,PRIMARY KEY(date,student_id)
,FOREIGN KEY(student_id)
REFERENCES student(student_id)
);

DROP TABLE extracurricular_score;
CREATE TABLE extracurricular_score(
student_id CHAR(5) NOT NULL
,academic_year VARCHAR(9) NOT NULL
,semester INTEGER NOT NULL
,input_date DATE NOT NULL
,extracurricular_score INTEGER NOT NULL
, PRIMARY KEY(academic_year, semester,student_id)
, FOREIGN KEY(student_id)
REFERENCES student(student_id)
);
